import React, { useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import CommentsInput from '../CommentsInput';
import {useFormik} from 'formik'
import { Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        width: '500px',
        height: '500px',
        border: '2px'
    },
    button: {
        width: '250px',
        height: '30px',
        background: 'green'
    }
}))


const CommentsInputForm = (comment) => {
    const classes = useStyles()

    useEffect(() => {
        console.log('In Form',comment)
    }, [comment, handleClick])

    const formik = useFormik({
        initialValues: {
          name: '',
          commentText: '',
        },
        onSubmit: values => {
            console.log('values: ', values.name)
        }
      });

      const handleClick = (name, text) => {
         comment = {name: name, text: text}
      }

    return <form className={classes.root} onSubmit={formik.handleSubmit}>
        <CommentsInput maxLength={20} value={formik.values.name} onChange={formik.handleChange} name="name"/>
        <CommentsInput maxLength={100} value={formik.values.commentText} onChange={formik.handleChange} name="commentText"/>
        <Button className={classes.button} onClick={handleClick(formik.values.name, formik.values.commentText)}>Создать комментарий</Button>
    </form>
}
export default CommentsInputForm
