import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

const useStyles = makeStyles(() => ({
    root: {
        padding: '3% 0',
        paddingLeft: '35%',
        color: 'white',
        position: 'relative',
        overflow: 'hidden'
    },
    car: {
        overflow: 'hidden',
        width: '941px',
    },
    "@keyframes moveTrees": {
        "100%": {
            transform: 'translateX(-1200px)'
        }
    },
    background: {
        width: '1500%',
        display: 'block',
        backgroundImage: 'url(trees.jpg)',
        animation: '$moveTrees 1s linear infinite',
        backgroundRepeat: 'repeat',
        zIndex: 1,
    },
    blur: {
        backdropFilter: 'blur(3px)',
        width: '1500%',
        height: '600px',
    },
    window: {
        position: 'absolute',
        top: '13px',
        width: '941px',
        height: '643px',
        background: 'url(window.png)',
    },
    studying: {
        position: 'absolute',
        // textShadow: '1px 3px 6px black, 0 0 1em black',
        // fontFamily: 'system-ui',
        fontSize: '43px',
        top: '20%',
        left: '7%',
        // fontWeight: 900
        color: '#fff',
        fontFamily: 'Arvo',
        fontWeight: 'bold',
        textShadow:
          '-3px -3px 0 #222, 3px -3px 0 #222, -3px 3px 0 #222, 3px 3px 0 #222, 4px 4px 0 #fff, 5px 5px 0 #fff, 6px 6px 0 #fff, 7px 7px 0 #fff',
        lineHeight: '0.8em',
        letterSpacing: '0.1em',
        transform: 'scaleY(0.7)',
        webkitTransform: 'scaleY(0.7)',
        mozTransform: 'scaleY(0.7)',
        margin: 0,
    },
    driving: {
        position: 'absolute',
        textShadow: '1px 2px 4px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '30px',
        top: '29%',
        left: '5%',
        fontWeight: 900
    },
    fromPro: {
        position: 'absolute',
        textShadow: '1px 3px 6px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '45px',
        top: '35%',
        left: '3%',
        fontWeight: 900
    }
}));

const AboutUs = (props) => {
    const classes = useStyles()

    return <div id='about' className={`${classes.root} ${props.className}`}>
        <div className={classes.car}>
        <div className={classes.background}>
            <div className={classes.blur}></div>
        </div>
        <div className={classes.window}></div>
        </div>
        <Typography className={classes.studying}>Обучение</Typography>
        <Typography className={classes.driving}>Вождению</Typography>
        <Typography className={classes.fromPro}>от ПРОфессионала</Typography>
    </div>
}

export default AboutUs
