        width: '500px',
        height: '500px',
        border: '2px'
    }
}))

const formik = useFormik({
    initialValues: {
      name: '',
    },
    validate,
    onSubmit: async (values) => {
      if (!loading) {
        setLoading(true);
        await onSubmit(values);
        setLoading(false);
      }
    },
  });

const CommentsInputForm = () => {
    const classes = useStyles()
    return <div className={classes.root}>
