import React, { useContext, useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import CommentsInputForm from '../components/CommentsInputForm';
import {observer} from 'mobx-react-lite'
import {Context} from '../index.js'
import {getComments} from '../http/httpHooks'

const useStyles = makeStyles((theme) => ({
    li: {
        color: 'green'
    }
}))

const CommentsPage = observer(() => {
    const classes = useStyles()
    const {comment} = useContext(Context)
    const [name, setName] = useState(null)
    const [createComment, setCreateComment] = useState(null)

    const handleClick = (name, commentText) => {
        setCreateComment({name: name, text: commentText})
     }

    useEffect(() => {
        getComments().then(data => comment.setComments(data))
        console.log('createComment', createComment)
    }, [createComment])



    return <div>
        <CommentsInputForm onClick={handleClick}/>
        {comment.comments.map(item => {
           return <li className={classes.li}key={item.name}>{item.name}</li>
        })}
    </div>
})

export default CommentsPage
