import {useState, useCallback} from 'react'


const getComments = () => {

    const request = useCallback(async(url) => {
        try {
            const response = await fetch(url)
            const data = await response.json()

            return data
        } catch (error) {
            console.log('Error: ', error)
        }
    }, [])

    return { request }
}

export default getComments
