import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import CommentsInput from '../CommentsInput';
import {useFormik} from 'formik'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        width: '500px',
        height: '500px',
        border: '2px'
    }
}))

const formik = useFormik({
    initialValues: {
      name: '',
      commentText: ''
    },
  });

const CommentsInputForm = () => {
    const classes = useStyles()
    return <form className={classes.root} onSubmit={formik.handleSubmit}>
        <CommentsInput maxLength={20} value={formik.values.name}/>
        <CommentsInput maxLength={100} value={formik.values.commentText}/>
    </form>
}
export default CommentsInputForm
