import {makeAutoObservable} from 'mobx'

export default class CommentsStore {
    constructor() {
        this._comments=[
            {name: 'one', text: 'one'},
            {name: 'two', text: 'two'}
        ]
        makeAutoObservable(this)
    }

    setComments(comments) {
        this._comments=comments
    }

    get comments() {
        return this._comments
    }
}
