import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

const useStyles = makeStyles(() => ({
    root: {
        padding: '10% 0',
        backgroundSize: 'cover',
        paddingLeft: '12%',
        color: 'white',
        position: 'relative'
    },
    background: {
        width: '900px',
        height: '570px',
        background: 'url(trees.jpg)',
        backgroundSize: '100%',
        backgroundRepeat: 'repeat-x',   
    },
    blur: {
        backdropFilter: 'blur(5px)',
        width: '900px',
        height: '570px',
    },
    window: {
        position: 'absolute',
        top: '118px',
        width: '900px',
        height: '600px',
        background: 'url(window.png)',
    },
    studying: {
        position: 'absolute',
        textShadow: '1px 3px 6px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '43px',
        marginTop: '12%',
        fontWeight: 900
    },
    driving: {
        position: 'absolute',
        textShadow: '1px 2px 4px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '30px',
        fontWeight: 900
    },
    fromPro: {
        position: 'absolute',
        textShadow: '1px 3px 6px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '45px',
        fontWeight: 900
    }
}));

const AboutUs = (props) => {
    const classes = useStyles()

    return <div id='about' className={`${classes.root} ${props.className}`}>
        <div className={classes.background}>
            <div className={classes.blur}></div>
        </div>
        <div className={classes.window}></div>
        <Typography className={classes.studying}>Обучение</Typography>
        <Typography className={classes.driving}>Вождению</Typography>
        <Typography className={classes.fromPro}>От Проффесионала</Typography>
    </div>
}

export default AboutUs
