import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

const useStyles = makeStyles(() => ({
    root: {
        padding: '10% 0',
        backgroundSize: 'cover',
        paddingLeft: '12%',
        color: 'white'
    },
    background: {
        background: 'url(sea.jpg)'
    },
    studying: {
        textShadow: '1px 3px 6px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '43px',
        marginTop: '12%',
        fontWeight: 900
    },
    driving: {
        textShadow: '1px 2px 4px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '30px',
        fontWeight: 900
    },
    fromPro: {
        textShadow: '1px 3px 6px black, 0 0 1em black',
        fontFamily: 'system-ui',
        fontSize: '45px',
        fontWeight: 900
    }
}));

const AboutUs = (props) => {
    const classes = useStyles()

    return <div id='about' className={`${classes.root} ${props.className}`}>
        <div className={classes.background}></div>
        <Typography className={classes.studying}>Обучение</Typography>
        <Typography className={classes.driving}>Вождению</Typography>
        <Typography className={classes.fromPro}>От Проффесионала</Typography>
    </div>
}

export default AboutUs
