    root: {
        border: '1.5px white'
    }
}))

const CommentsInput = (props) => {
    const classes = useStyles()
    return <TextField
    className={classes.root} 
    value={props.value}
    autoComplete="off"
    variant="outlined"
    InputProps={{
        inputProps: { maxLength: props.maxLength },
        classes: {

        }
      }}
    />
}
