import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import CommentsInput from '../CommentsInput';
import {useFormik} from 'formik'
import { Button, Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        width: '700px',
        border: '2px',
        background: '#1C2128',
        padding: '1% 0'
    },
    rootMobile: {
        width: '500px'
    },
    button: {
        width: '250px',
        height: '50px',
        background: '#30D171',
        alignSelf: 'center',
        marginTop: '1%',
        fontWeight: 'bold',
        fontSize: '15px'
    },
    headText: {
        marginLeft: '2%',
        color: 'white',
        fontSize: '20px',
        fontWeight: 'bold'
    },
    nameInput: {
        width: '275px',
    },
    review: {
        width: '690px'
    },
}))


const CommentsInputForm = (props) => {
    const classes = useStyles()
    const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })

    const formik = useFormik({
        initialValues: {
          name: '',
          commentText: '',
        },
        onSubmit: (values) => {
            props.onSubmit(values.name, values.commentText)
        }
      });

    return <form className={`${classes.root} ${isTabletOrMobile  && classes.rootMobile}`} onSubmit={formik.handleSubmit}>
        <div>
            <Typography className={classes.headText}>Имя</Typography>
            <CommentsInput style={classes.nameInput} maxLength={20} value={formik.values.name} onChange={formik.handleChange} name="name" fullWidth={false}/>
        </div>
        <div className={classes.review}>
            <Typography className={classes.headText}>Отзыв</Typography>
            <CommentsInput style={classes.textInput} rows={6} maxLength={300} value={formik.values.commentText} onChange={formik.handleChange} name="commentText" fullWidth={true}/>
        </div>
        <Button className={classes.button} onClick={props.onClick} type='submit'>Создать комментарий</Button>
    </form>
}

export default CommentsInputForm
